INSERT INTO public.document(title, text, date, time) VALUES ('Ян I Схоластик (Освенцимский)','Ян I Схоластик (Освенцимский) (польск. Jan I Scholastyk (oświęcimski); (1308/1310 — 29 сентября 1372) — князь освенцимский (1321/1324 — 1372), единственный сын и преемник князя освенцимского Владислава I Освенцимского от брака с Евфросиньей Мазовецкой (1292—1328/1329). Представитель Силезской линии Пястов.','03.07.2015','09:23:40');
INSERT INTO public.document(title, text, date, time) VALUES ('Космос-119','Ко́смос-119 («ДС-У2-И» № 1) — советский научно-исследовательский спутник серии космических аппаратов «Космос» типа «ДС-У2-И», запущенный для комплексного исследования характеристик ионосферы Земли и электромагнитного излучения околоземного космического пространства.В ходе эксперимента кроме данного спутника («Космос-119» был первый из данной серии) было запущено ещё два спутника аналогичной конструкции:

