INSERT INTO public.document(title, text, date, time) VALUES ('Чемпионат Европы по боксу 2015','41-й Чемпионат Европы по боксу 2015 года прошёл в Самокове (Болгария) с 6 по 15 августа. В качестве организаторов чемпионата выступили Европейская конфедерация бокса (ЕКБ) и Болгарская федерация бокса.','05.05.2012','23:18:27');
