INSERT INTO public.document(title, text, date, time) VALUES ('Фиджи на летних Олимпийских играх 1988','Фиджи принимали участие в Летних Олимпийских играх 1988 года в Сеуле (Корея) в седьмой раз за свою историю, но не завоевали ни одной медали. Сборную страны представляли 23 спортсмена (в том числе - 4 женщины), принимавших участие в соревновавниях по боксу, дзюдо, лёгкой атлетике, парусному спорту и плаванию.','15.04.2019','12:35:13');
