INSERT INTO public.document(title, text, date, time) VALUES ('Тузла (остров)','Ту́зла (крымско-тат. Tuzla, Тузла, укр. Тузла, от тюрк. tuz «соль», тур., кыпч. tuzlu «солёный») — песчаный остров площадью 3,5 км², длиной от 5 до 6,5 км и шириной около 0,5 км в Керченском проливе Азовского моря между Керченским полуостровом Крыма на западе и Таманским полуостровом Краснодарского края на востоке.','18.02.2019','09:46:14');
