INSERT INTO public.document(title, text, date, time) VALUES ('Берцимас, Димитрис','Дими́трис Джон Берци́мас (греч. Δημήτρης Μπερτσιμάς, англ. Dimitris John Bertsimas; род. 3 октября 1962, Александруполис, Восточная Македония и Фракия, Греция) — греко-американский математик, специалист в области прикладной математики, признанный пионер предсказательной аналитики в здравоохранении. Профессор Школы менеджмента имени Слоуна Массачусетского технологического института (MIT Sloan), содиректор Центра исследования операций MIT (2006—н. в.). Член Американского математического общества, Общества промышленной и прикладной математики, Общества математического программирования, Эконометрического общества, Американской финансовой ассоциации, Института математической статистики, Национальной инженерной академии (2005), Института исследования операций и управленческих наук (2007). Председатель мандатной комиссии Группы греческих учёных Бостона.
h-индекс = 77, процитирован > 29 220 раз.','14.06.2013','08:06:49');
INSERT INTO public.document(title, text, date, time) VALUES ('Правительство Федерации Боснии и Герцеговины','Президент правительства (премьер) Федерации Боснии и Герцеговины (босн. premijer Federacije Bosne i Hercegovine, хорв. predsjednik Vlade Federacije Bosne i Hercegovine, серб. председник Владе Федерације Босне и Херцеговине) является главой правительства Федерации Боснии и Герцеговины. Как и весь состав правительства 16 министров, он назначается Президентом Федерации Боснии и Герцеговины с одобрения двух (до 2003 года одного) вице-президентов, с учётом национальных квот: министрами должны быть 8 босняков, 5 хорватов и 3 серба. Из числа министров назначаются два заместителя президента правительства, вместе с главой правительства представляющие три конституционных народа федерации. Состав правительства подлежит утверждению Палатой представителей Парламента федерации.','24.05.2014','23:02:04');
