INSERT INTO public.document(title, text, date, time) VALUES ('Жирардо, Ипполит','Ипполи́т Жирардо́ (фр. Hippolyte Girardot, настоящее имя Фредери́к Жирардо́, Frédéric Girardot ; род. 10 октября 1955, Булонь-Бийанкур, Франция) — французский актёр театра и кино.
