INSERT INTO public.document(title, text, date, time) VALUES ('Д’Аркур, Франсуа','Франсуа д’Аркур (фр. François d''Harcourt; 6 ноября 1689 — 10 июля 1750, Сен-Жермен-ан-Ле) — французский военачальник, 2-й герцог д’Аркур, пэр и маршал Франции, рыцарь орденов короля (16.05.1728).','15.04.2012','19:18:30');
INSERT INTO public.document(title, text, date, time) VALUES ('Грицай, Евдокия Павловна','Евдокия Павловна Грицай (укр. Євдокія Павлівна Грицай; 2 марта 1928 год, село Нехвороща, Полтавская область, Украинская ССР — 28 августа 2006 год, село Нехвороща, Новосанжарский район, Полтавская область, Украина) — колхозница, звеньевая колхоза «XXVI партсъезд» Нехворощанского района Полтавской области, Украинская ССР. Герой Социалистического Труда (1948).','31.10.2018','14:26:27');
