INSERT INTO public.document(title, text, date, time) VALUES ('Конькобежный спорт на Олимпийских играх','Конькобежный спорт относится к олимпийским видам спорта с 1924 года. Женские соревнования проводятся с 1960 года, после того как женщины состязались в показательных забегах на Олимпиаде 1932.
На настоящий момент на Олимпийских играх разыгрываются по 7 комплектов медалей для мужчин и женщин. Самый  молодой вид — масс-старт — был добавлен на Играх 2018 в Пхёнчхане.','30.08.2018','10:44:18');
