INSERT INTO public.document(title, text, date, time) VALUES ('Электронная томография','Электронная томография (англ. electron microscopy tomography, scanning transmission electron tomography, 3D TEM, ET) — методика получения трехмерных изображений наноразмерных/субмикроразмерных структур с помощью просвечивающей электронной микроскопии. Трехмерная модель реконструируется из изображений, полученных с одной и той же частицы под разными углами.','19.08.2012','22:14:58');
