INSERT INTO public.document(title, text, date, time) VALUES ('Клопов, Эдуард Викторович','Эдуа́рд Ви́кторович Кло́пов (10 августа 1930, Днепропетровск — 24 августа 2010, Москва, Россия) — советский и российский социолог, специалист в области социальной структуры, социально-экономических и политических проблем наёмного труда. Доктор исторических наук, профессор. Работал в нескольких институтах, затем в ИМЭМО РАН, был соредактором издания «Социально-трудовые исследования».','12.02.2013','16:05:15');
