INSERT INTO public.document(title, text, date, time) VALUES ('Национальный Полоцкий историко-культурный музей-заповедник','Национальный Полоцкий историко-культурный музей-заповедник (белор. Нацыянальны Полацкi гісторыка-культурны музей-запаведнік)- объединение музеев и охраняемых территорий города Полоцка. Заповедник имеет республиканское значение и областное подчинение. Территория заповедника составляет 64,7 га.','24.06.2015','01:04:04');
INSERT INTO public.document(title, text, date, time) VALUES ('Постлетуэйт, Пит','Питер Уильям «Пит» Постлетуэйт (англ. Peter William "Pete" Postlethwaite [ˈpɒsəlθweɪt]; 7 февраля 1946, Уоррингтон — 2 января 2011, Шрусбери) — английский киноактёр. Наиболее известные фильмы с его участием — «Подозрительные лица», «Во имя отца», «Парк Юрского периода 2: Затерянный мир», «Последний из Могикан», «Сердце дракона», «Начало», «Город воров». В 1994 году номинировался на «Оскар» за лучшую мужскую роль второго плана в фильме «Во имя отца».','31.01.2016','18:48:52');
