INSERT INTO public.document(title, text, date, time) VALUES ('Атабаски Аляски','Атабаски Аляски или Атапаски Аляски — коренные жители Аляски; индейцы северо-атабаскской этнолингвистической группы, проживающие на территории внутренних районов штата Аляска (США).
