INSERT INTO public.document(title, text, date, time) VALUES ('Apple A8X','Apple A8X — 64-битный 3-х ядерный ARM-микропроцессор компании Apple из серии Apple Ax. Реализует набор инструкций ARMv8-A, используется в планшетах iPad Air 2. Процессор во многом аналогичен процессору A8 и так же относится по формулировке Apple к классу десктопных процессоров (Desktop Processor), благодаря многозадачности и широкому спектру выполняемых задач.','25.12.2018','08:40:26');
