INSERT INTO public.document(title, text, date, time) VALUES ('Говорков, Виктор Иванович','Ви́ктор Ива́нович Го́ворков (5 (18) ноября 1906, Владивосток, Российская империя — 13 июня 1974, Москва, СССР) — советский художник двухмерного пространства: живописец, график, плакатист, иллюстратор. Член Союза художников СССР. Заслуженный художник РСФСР (1971). Автор множества известных советских политических и социальных плакатов 1930—1960 годов.','08.01.2018','06:41:35');
