INSERT INTO public.document(title, text, date, time) VALUES ('Мингату','Мингату (кит. трад. 明安圖, упр. 明安图, пиньинь: Ming Antu; монг. Шарайн Мянгат, Minggatu) (ок. 1685/86 или 1692 — 1764/1765) — монгольский математик и астроном при маньчжурском дворе. Входит в четвёрку самых заметных математиков империи Цин своего времени, первым в ряду трёх других — Дун Ючэн (1791-1823), Сян Минда (1789-1850) и Дай Сю (1805-1860).','05.10.2014','06:27:27');
