INSERT INTO public.document(title, text, date, time) VALUES ('Сарычев, Фёдор Васильевич','Фёдор Васильевич Сарычев (3 [15] января 1829, Санкт-Петербург, Российская Империя — 27 марта [4 апреля] 1884, Гдов, Гдовский уезд, Санкт-Петербургская губерния, Российская империя) — русский контр-адмирал, участник Крымской войны, управляющий двора великого князя Константина Николаевича.','28.07.2017','16:15:29');
