INSERT INTO public.document(title, text, date, time) VALUES ('Горегляд, Харитон Степанович','Горегляд Харитон Степанович (11 (23) октября 1898—23 мая 1985) — советский учёный в области ветеринарии, доктор ветеринарных наук, профессор (1939), академик АН БССР (с 1950) и действительный член Академии сельскохозяйственных наук БССР (с 1957). Заслуженный деятель науки БССР (1949). Участник Гражданской войны.','19.10.2012','06:03:33');
