INSERT INTO public.document(title, text, date, time) VALUES ('Демократическо-республиканская партия','Демократическо-республиканская партия (англ. Democratic-Republican Party) — политическая партия США, существовавшая с 1792 по 1825 гг. Партия была основана около 1792 года Томасом Джефферсоном и Джеймсом Мэдисоном в противовес Федералистской партии Александра Гамильтона. Демократическо-республиканская партия стала постепенно доминирующей политической силой в Соединённых Штатах. В 1820-х годах после роспуска Федералистской партии партийная дисциплина потеряла свою актуальность и республиканцы распались на несколько фракций, одна из которых дала начало современной Демократической партии, а другая — Национальной республиканской партии.','28.10.2016','06:20:31');
