INSERT INTO public.document(title, text, date, time) VALUES ('Чемпионат мира по волейболу среди мужчин 1982','10-й чемпионат мира по волейболу среди мужчин прошёл со 1 по 15 октября 1982 года в четырёх городах Аргентины с участием 24 национальных сборных команд. Чемпионский титул в 6-й раз в своей истории выиграла сборная СССР.','25.06.2014','11:40:59');
