INSERT INTO public.document(title, text, date, time) VALUES ('Заубер','Sauber Motorsport AG — швейцарская компания по производству спортивных и гоночных автомобилей и автогоночная команда, основанная в 1970 году Петером Заубером. В 1980-х годах успешно выступала в гонках спортпрототипов, производя шасси для команды Mercedes-Benz. Выступала в чемпионате мира по автогонкам в классе Формула-1 с 1993 года по 2018 год. С момента дебюта команды в Формуле-1 ни один из её пилотов ни разу не побеждал в гонках и не завоёвывал поул-позишн. Лучшими результатами стали два вторых места в гонках, занятых Серхио Пересом в сезоне 2012 года, и два старта с первого ряда Жана Алези на трассе А1-Ринг (1998) и Камуи Кобаяси на трассе Спа-Франкоршам (2012).','03.09.2013','03:50:12');
