INSERT INTO public.document(title, text, date, time) VALUES ('Крус, Доминик','Доминик Рохелио Крус (англ. Dominick Rojelio Cruz; род. 9 марта 1985 года, Тусон, Аризона, США) — американский боец смешанных боевых искусств мексиканского происхождения, выступающий под эгидой UFC в легчайшей весовой категории. Бывший двукратный чемпион UFC и бывший чемпион WEC в легчайшем весе. По состоянию на ноябрь 2015 года он занимал третью строчку в легчайшем весе по версии сайта Sherdog.','03.03.2019','06:41:48');
