INSERT INTO public.document(title, text, date, time) VALUES ('Моши, Магдалена','Магдалена Рут Алекс Моши (суахили Magdalena Ruth Alex Moshi; род. 30 ноября 1990) — танзанийская пловчиха, специалистка по плаванию вольным стилем. Выступала за сборную Танзании по плаванию в период 2007—2016 годов, участница многих крупных международных соревнований, в том числе трёх летних Олимпийских игр.','28.02.2019','14:01:23');
INSERT INTO public.document(title, text, date, time) VALUES ('Басаев, Шамиль Салманович','Шами́ль Салма́нович Баса́ев, он же Абдалла́х Шами́ль Абу́-Идри́с (14 января 1965, Дышне-Ведено, Чечено-Ингушская АССР, РСФСР, СССР — 10 июля 2006, Экажево, Ингушетия, Российская Федерация) — чеченский террорист, активный участник вооружённой борьбы за выход непризнанной Чеченской Республики Ичкерия из состава Российской Федерации и боевых действий в Чечне в 1991—2006 годах, один из руководителей самопровозглашённой Чеченской Республики Ичкерия (ЧРИ). Имел звание генерала армии ЧРИ (посмертно присвоено звание генералиссимуса ЧРИ). Организовал ряд резонансных террористических актов на территории Российской Федерации. Был внесён в списки террористов ООН, Госдепартамента США и Европейского союза. Выходец из тейпа Ялхорой (по другим данным — Белгатой). Уничтожен в ночь на 10 июля 2006 года в районе села Экажево.','27.06.2014','12:49:02');
INSERT INTO public.document(title, text, date, time) VALUES ('Белла чао','«Белла чао» (итал. Bella ciao — Прощай, красавица) — народная итальянская песня, исполнявшаяся участниками движения Сопротивления в Моденских горах во время Второй мировой войны и получившая широкую мировую известность в конце 1940-х годов.','08.12.2016','06:13:00');
