INSERT INTO public.document(title, text, date, time) VALUES ('Полуэктов, Павел Андреевич','Па́вел Андре́евич Полуэ́ктов (20 января 1992, Серов, Россия) — казахстанский и российский хоккеист, вратарь.','06.06.2017','03:53:31');
INSERT INTO public.document(title, text, date, time) VALUES ('Власов, Трофим Леонтьевич','Трофим Леонтьевич Власов (1901—1941) — генерал-майор артиллерии РККА, участник польского похода, советско-финской и Великой Отечественной войн.','23.12.2016','22:28:33');
