INSERT INTO public.document(title, text, date, time) VALUES ('Релига, Збигнев','Зби́гнев Эуге́ниуш Рели́га (польск. Zbigniew Eugeniusz Religa; 16 декабря 1938 — 8 марта 2009) — польский кардиохирург и политик.
