INSERT INTO public.document(title, text, date, time) VALUES ('Санто-Доминго-де-лос-Тсачилас','Санто-Доминго-де-лос-Тсачилас (исп. Santo Domingo de los Tsáchilas) — одна из провинций Эквадора. Площадь составляет 3 805 км². Население по данным на 2010 год — 410 937 человек; плотность населения — 108,00 чел./км². Административный центр — город Санто-Доминго.','19.05.2017','01:49:52');
