INSERT INTO public.document(title, text, date, time) VALUES ('Мэнн, Фрэнк','Фрэнк Дру́ри Мэнн (англ. Frank Drury Mann; 17 марта 1891 — июль 1966) — английский футболист, крайний хавбек, наиболее известный по выступлениям за клубы «Хаддерсфилд Таун» и «Манчестер Юнайтед».','04.10.2018','09:29:23');
