INSERT INTO public.document(title, text, date, time) VALUES ('Скатол','Скато́л (от др.-греч. σκῶρ, род.п. σκατός «экскременты», 3-метилиндол, 4-метил-2,3-бензпиррол) — органическое гетероциклическое соединение, производное индола (в-метилиндол). Бесцветные кристаллы с очень неприятным запахом.','24.08.2016','18:19:17');
INSERT INTO public.document(title, text, date, time) VALUES ('Бендорф (Гольштейн)','Бендорф (нем. Bendorf) — коммуна в Германии, в земле Шлезвиг-Гольштейн.
