INSERT INTO public.document(title, text, date, time) VALUES ('Чудинов, Александр Николаевич','Алекса́ндр Никола́евич Чуди́нов (26 февраля (10 марта) 1843, Полтавская губерния — 14 декабря 1908) — русский писатель, переводчик, редактор, педагог и просветитель.','19.06.2013','06:31:24');
INSERT INTO public.document(title, text, date, time) VALUES ('Мин шилу','«Мин шилу» (кит. трад. 明實錄, упр. 明实录, пиньинь: Míng shí-lù), или «Доподлинные хроники династии Мин» (также, «Правдивые записи о правлении династии Мин»,
«Правдивые записи [династии] Мин») — собрание хроник правления императоров Династии Мин, правивших в Китае с 1368 по 1644 г. Один из основных источников по истории Китая в этот период.','19.09.2017','10:03:17');
INSERT INTO public.document(title, text, date, time) VALUES ('Бингулац, Петар','Петар Бингулац (серб. Петар Бингулац; 5 июля 1897, Вуковар — 4 января 1990, Белград) — сербский музыковед, музыкальный педагог и критик.
Получил богословское образование в Сремских-Карловцах (1919), затем изучал право в Парижской высшей школе политических наук и музыку в парижской Schola cantorum у Венсана д’Энди (композиция) и Поля Ле Флема (контрапункт). В 1925—1945 гг. находился на дипломатической службе Королевства сербов, хорватов и словенцев, работая в Милане, Праге и Софии. Затем вернулся в Югославию и посвятил себя полностью работе в области музыки.
Бингулац преподавал теоретические и хоровые дисциплины Белградской академии музыки (среди его учеников, в частности, Рудольф Бруччи), выступал как музыкальный критик в белградской периодике. Опубликовал монографию «Творчество Стевана Мокраняца» (серб. Дело Стевана Мокрањца; 1981), избранные работы составили сборник «Статьи о музыке, этюды и очерки» (серб. Написи о музици, студије и есеји; 1988). Был членом Союза фольклористов Югославии, посвятил ряд работ истории церковного пения у балканских народов.','15.10.2012','00:41:06');
