INSERT INTO public.document(title, text, date, time) VALUES ('Кампуш, Эдуарду','Эдуарду Энрике Ассиоли Кампуш (порт. Eduardo Henrique Accioly Campos; 10 августа 1965, Ресифи, Пернамбуку — 13 августа 2014, Сантус, Сан-Паулу) — бразильский политический и государственный деятель. губернатор штата Пернамбуку (2007—2014), в 2010 году переизбранный на свой пост с более чем 80 % голосов избирателей.
Выросший в семье активных политических деятелей, борцов против режима военной диктатуры и приверженцев социалистического пути развития страны, Эдуарду Кампуш сам стал общенациональным лидером Социалистической партии и кандидатом на пост президента на всеобщих выборах 2014 года.
13 августа Кампуш погиб в авиакатастрофе в городе Сантус. В качестве кандидата социалистов его сменила Марина Силва.','24.07.2013','14:37:51');
INSERT INTO public.document(title, text, date, time) VALUES ('Саут-Ферри (значения)','Саут-Ферри (англ. South Ferry — Южный паром):

