INSERT INTO public.document(title, text, date, time) VALUES ('Ульрика Луиза Сольмс-Браунфельсская','Ульрика Луиза Сольмс-Браунфельсская (нем. Ulrike Luise zu Solms-Braunfels; 1 мая 1731, Хунген — 12 сентября 1792, Гомбург) — графиня Сольмс-Браунфельсская, в замужестве ландграфиня Гессен-Гомбургская, регент Гессен-Гомбурга в 1751—1766 годах.','14.04.2018','18:58:48');
INSERT INTO public.document(title, text, date, time) VALUES ('Окушковщина','Оку́шковщина (белор. Аку́шкаўшчына, польск. Okuszkowszczyzna) — хутор в Сморгонском районе Гродненской области Белоруссии.
