INSERT INTO public.document(title, text, date, time) VALUES ('Дом Висенс','Дом Висенс (кат. Casa Vicens) — частный жилой дом, построенный в 1883—1885 годах в Барселоне архитектором Антони Гауди для семьи Мануэля Висенса, одна из достопримечательностей каталонской столицы. Здание расположено по адресу: улица Каролинас (кат. Carrer de les Carolines), 22-24, в районе Барселоны Грасия и является первым крупным заказом Гауди.','05.07.2017','15:12:57');
