INSERT INTO public.document(title, text, date, time) VALUES ('Луизиана (Парана)','Луизиана (порт. Luiziana)  —  муниципалитет в Бразилии, входит в штат Парана. Составная часть мезорегиона Западно-центральная часть штата Парана. Входит в экономико-статистический  микрорегион Кампу-Моран. Население составляет 6141 человек на 2006 год. Занимает площадь 908,604 км². Плотность населения — 6,8 чел./км².','13.02.2018','22:32:01');
