INSERT INTO public.document(title, text, date, time) VALUES ('Пустынная Пятница','Пустынная Пятница — деревня в Смоленской области России, в Сафоновском районе. Население – 0 жителей (2007 год) . Расположена в центральной части области в 25 км к юго-востоку от города Сафонова, в 4 км севернее автодороги Р134 «Старая Смоленская дорога». В 15 км севернее от деревни железнодорожная станция Издешково	 на линии Москва — Минск. Входит в состав Игнатковского сельского поселения.','26.06.2014','09:09:53');
