INSERT INTO public.document(title, text, date, time) VALUES ('Тойрделбах Уа Бриайн','Тойрделбах Уа Бриайн (Турла O’Брайн; ирл. Toirdelbach Ua Briain, Turlough O’Brien; 1009 — 14 июля 1086) — король Мунстера (1064—1086) и верховный король Ирландии (1072—1086). Внук верховного короля Ирландии Бриана Боройме (Бриана Бору), сын Тадга мак Бриайна (ирл. — Tadc mac Briain), который был убит в 1023 году его сводным братом Доннхадом мак Бриайном.','11.08.2018','15:20:49');
