INSERT INTO public.document(title, text, date, time) VALUES ('Вторая фитна','Вторая фитна — период масштабных политических и военных столкновений, поразивший Омейядский халифат после смерти первого омейядского халифа Муавии I. Историки определяют началом Второй фитны 680 год, а конец варьируется от 685 до 692 года. Война ознаменовалась появлением двух разных оппозиционных групп династии Омейядов. В первую вошли Хусейн ибн Али и его сторонники. Во вторую — Абдуллах ибн аз-Зубайр.','21.08.2013','08:13:25');
