INSERT INTO public.document(title, text, date, time) VALUES ('Бертольд IV (герцог Церинген)','Бертольд IV Церингенский (нем. Berthold IV. von Zähringen; ок. 1125 — 8 декабря 1186) — герцог Церингенский с 1152 года, сын Конрада Церингенского и Клеменции Намюрской. Он основал многочисленные города, в том числе Фрибур (ныне в Швейцарии).','03.02.2016','14:51:08');
INSERT INTO public.document(title, text, date, time) VALUES ('Пометка заключённых в концентрационных лагерях','Пометка заключённых в концентрационных лагерях Третьего рейха использовалась для группирования и социальной стигматизации заключённых. Соответствующие эмблемы должны были облегчать лагерному персоналу идентификацию заключённых по странам происхождения, расе, приговору и т. д. Номер заключённого заменял в лагере имя.','07.06.2017','07:20:09');
