INSERT INTO public.document(title, text, date, time) VALUES ('Герсдорф, Рудольф-Кристоф фон','Барон Ру́дольф-Кри́стоф фон Ге́рсдорф (нем. Rudolf-Christoph Freiherr von Gersdorff, 27 марта 1905, Любин — 27 января 1980, Мюнхен) — активный участник немецкого движения Сопротивления и планов покушения на Гитлера, инициатор создания организации Йоханнитер-скорая-помощь.','24.08.2018','18:10:52');
