INSERT INTO public.document(title, text, date, time) VALUES ('8 Лисички','8 Лисички – оранжевый гигант (спектральный класс K0III) видимой величины 5,81, удалённый от Земли на расстояние 484 световых лет. Образует широкую оптически-двойную звезду с Альфой этого созвездия. Альфа созвездия- Anser (с латыни "гусь") произошло название из старого названия Vulpecula et Anser (лисица с гусём). Первое изучение и добавление на звёздные карты провёл Ян Гевелий в 1687 году.','26.02.2018','05:18:21');
INSERT INTO public.document(title, text, date, time) VALUES ('Ozyptila claveata','Ozyptila claveata (лат.) — вид пауков семейства Пауки-бокоходы (Thomisidae). Встречается в Палеарктике (Европа, Россия, Турция). Длина тела около 3 мм (самки от 3 до 4, самцы от 2 до 3 мм). Основная окраска коричневая с примесью белых и жёлтых отметин (просома одноцветная тёмная). Встречаются на влажных лугах. 
Голени первой пары ног вентрально с двумя парами шипов; волоски заострённые или булавовидные. Педипальпы самцов с тегулярным апофизом, эпигинум с чехлом. Первые две пары ног развернуты передними поверхностями вверх, заметно длиннее ног третьей и четвёртой пар. Паутину не плетут, жертву ловят своими видоизменёнными передними ногами.','02.01.2016','11:02:42');
