INSERT INTO public.document(title, text, date, time) VALUES ('Сард, Ален','Ален Сард (фр. Alain Sarde; род. 28 марта 1952 в Булонь-Бийанкур) — один из видных французских продюсеров (более двухсот фильмов); владелец двух кинокомпаний — «Сара Фильм» (1975) и «Сард Фильм» (1983).
