INSERT INTO public.document(title, text, date, time) VALUES ('Коусал, Роберт','Роберт Коусал (7 октября 1990, Вишков, Чехия) — чешский хоккеист, нападающий шведского клуба «Брюнес». Воспитанник хоккейного клуба «Пардубице». Игрок сборной Чехии.','24.10.2018','03:35:16');
INSERT INTO public.document(title, text, date, time) VALUES ('Пильва (Чердынский район)','Пильва — посёлок в Чердынском районе Пермского края. Входит в состав Вильгортского сельского поселения.','02.02.2018','20:42:21');
INSERT INTO public.document(title, text, date, time) VALUES ('Ильяна, Адольфо Тито','Адольфо Тито Ильяна (исп. Adolfo Tito Yllana; род. 6 февраля 1948, Нага, Филиппины) — филиппинский прелат и ватиканский дипломат. Титулярный архиепископ Монтекорвино с 13 декабря 2001. Апостольский нунций в Папуа — Новой Гвинее с 13 декабря 2001 по 31 марта 2006. Апостольский нунций на Соломоновых Островах с 5 февраля 2002 по 31 марта 2006. Апостольский нунций в Пакистане с 31 марта 2006 по 20 ноября 2010. Апостольский нунций в Демократической Республике Конго с 20 ноября 2010 по 17 февраля 2015. Апостольский нунций в Австралии с 17 февраля 2015.','23.04.2012','05:04:20');
INSERT INTO public.document(title, text, date, time) VALUES ('Суперкубок Италии по футболу 2009','Суперкубок Италии по футболу 2009 (итал. Supercoppa italiana 2009) — 22-й розыгрыш Суперкубка Италии, в котором встречались чемпион страны сезона 2009/10 миланский «Интер» и обладатель Кубка Италии 2008/09 «Лацио». Встреча состоялась 8 августа 2009 года в Пекине, на Пекинском национальном стадионе.','25.01.2013','08:09:13');
