INSERT INTO public.document(title, text, date, time) VALUES ('Эсменар, Жозеф Альфонс','Жозеф Альфонс Эсменар (1770—1811) — французский поэт.
Эмигрировал после 10 августа 1792 г.; возвратился в Париж в 1797 г.; писал в «Quotidienne», опять бежал; при Наполеоне сделался театральным цензором.
За обширную дидактическую поэму «La Navigation» (1805), заключающую несколько хороших описаний, но в общем вялую и безжизненную, он был избран в члены французской академии. Написал ещё пьесы «Trajan» (1807) и «Cortez» (1809), а также «Couronne poétique de Napoléon» (1807).','15.01.2016','16:17:17');
INSERT INTO public.document(title, text, date, time) VALUES ('Наумов, Николай Александрович (губернатор)','Никола́й Алекса́ндрович Нау́мов (ок. 1780 — ок. 1823) — российский государственный деятель, 12-й губернатор Рязанской губернии, глава города Таганрог.','08.01.2018','18:43:28');
