INSERT INTO public.document(title, text, date, time) VALUES ('Чемпионат Сербии и Черногории по футболу 2005/2006','Чемпионат Сербии и Черногории по футболу 2005/2006 (серб. Прва лига Србије и Црне Горе у фудбалу 2005/06.) — третий и последний сезон Первой футбольной лиги Сербии и Черногории, организованный под эгидой Футбольного союза Сербии и Черногории. В июне 2006 года Государственный союз Сербии и Черногории прекратил своё существование, а вместе с ним и единая футбольная лига Сербии и Черногории. В турнире принимали участие 16 команд. Сезон стартовал 5 августа 2005 года и завершился 6 мая 2006 года. Чемпионат проходил по двухкруговой системе.','13.09.2018','06:13:08');
