INSERT INTO public.document(title, text, date, time) VALUES ('Пермская государственная фармацевтическая академия','Пе́рмская госуда́рственная фармацевти́ческая акаде́мия (ПГФА) (полное название: Государственное бюджетное образовательное учреждение высшего профессионального образования «Пермская государственная фармацевтическая академия») — высшее учебное заведение в Перми, готовящее специалистов с фармацевтическим образованием.
