INSERT INTO public.document(title, text, date, time) VALUES ('Злота (культура)','Культу́ра Зло́та, культу́ра Зло́ты (польск. kultura Złocka) — археологическая культура эпохи позднего неолита — начала бронзового века (2200—1700 гг. до н. э.). Принадлежит группе культур шнуровой керамики (польск. kultura ceramiki sznurowej), также называемой культурой боевых топоров (польск. kultura toporów bojowych).','05.08.2015','14:18:36');
