INSERT INTO public.document(title, text, date, time) VALUES ('Лумис, Фрэнк','Фрэнк Лумис — американский легкоатлет. На олимпийских играх 1920 года выиграл золотую медаль на дистанции 400 метров с барьерами с мировым рекордом — 54,0. Чемпион США в беге на 200 метров с барьерами в 1917 и 1918 годах и чемпион США на дистанции 400 метров с барьерами в 1920 году.
Начал заниматься лёгкой атлетикой во время учёбы в школе Орегона, штат Иллинойс, где стал чемпионом США среди школьников в беге на 220 ярдов с барьерами.','18.10.2018','22:33:19');
INSERT INTO public.document(title, text, date, time) VALUES ('Белогорка (Тюхтетский район)','Белогорка — деревня в Тюхтетском районе Красноярского края России. Входит в состав Зареченского сельсовета. Находится на берегах реки Тайлок (приток реки Четь), примерно в 18 км к северо-северо-западу (NNW) от районного центра, села Тюхтет, на высоте 167 метров над уровнем моря.','23.12.2016','17:39:42');
