INSERT INTO public.document(title, text, date, time) VALUES ('Берегиня (газета)','«Береги́ня» — российская газета на русском языке, издание общественного экологического движения в защиту Волги «Поможем реке» под эгидой Международного социально-экологического союза, основное СМИ экологических активистов Поволжья и МСоЭС. Одна из старейших экологических газет России.','10.04.2012','05:37:51');
