INSERT INTO public.document(title, text, date, time) VALUES ('Валериана лекарственная','Валериа́на лека́рственная, или Коша́чья трава́ (лат. Valeriána officinális) — вид растений рода Валериана (Valeriana) подсемейства Валериановые. В медицинских целях используют корневище и корни растения. Применяется в качестве седативного лекарственного средства и как спазмолитик (в отношении гладкой мускулатуры органов желудочно-кишечного тракта (ЖКТ) и мочевыделительной системы). Обладает также желчегонным действием, увеличивает секрецию ЖКТ, расширяет коронарные сосуды.','25.08.2015','14:47:39');
INSERT INTO public.document(title, text, date, time) VALUES ('Иванов, Вадим Геннадьевич','Вадим Геннадьевич Иванов (17 июля 1943, Нижний Тагил, РСФСР, СССР — 6 ноября 1996, Вологда, Россия) — советский футболист, защитник, полузащитник и тренер. Мастер спорта (с 1964).','03.03.2012','02:16:38');
