INSERT INTO public.document(title, text, date, time) VALUES ('K2-72 e','K2-72 e — экзопланета у звезды K2-72, находящейся в созвездии Водолея на расстоянии примерно 227,7 световых лет (69,8 парсек или почти 2,1538⋅1015 км) от Солнца.
