INSERT INTO public.document(title, text, date, time) VALUES ('Сибли, Антуанетт','Антуане́тт Си́бли (англ. Antoinette Sibley; род. 27 февраля 1939 года) — английская артистка балета, прима-балерина лондонского Королевского балета с 1960 года. После ухода со сцены в 1991 году возглавила Королевскую академию танца. Дама Ордена Британской империи.','25.12.2012','00:43:48');
INSERT INTO public.document(title, text, date, time) VALUES ('Маслоньске-Наталин','Маслоньске-Наталин (польск. Masłońskie Natalin) — остановочный пункт в селе Маслоньске в гмине Порай, в Силезском воеводстве Польши. Имеет 2 платформы и 2 пути.
