INSERT INTO public.document(title, text, date, time) VALUES ('Флэтвудс (Западная Виргиния)','Флэтвудс (англ. Flatwoods) — город в штате Западная Виргиния США. Находится в округе Брэкстон. В 2010 году в городе проживало 277 человек. Город стал известен после появления Флэтвудского монстра в 1952 году.','12.06.2017','16:09:41');
